import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { startTest } from '../app/verbsSlice';

// import Selection from './Selection';
// import Test from './Test';

import AltSelection from './AltSelection';
import AltTest from './AltTest';

const AltVerbs = () => {

   const [displayQuestion, setDisplayQuestion] = useState(false)

   const updatePage = () => {
      document.getElementById('selection').classList.add('hidden')
      setDisplayQuestion(true)
   }

   return (
      <div id='verbs'>
         { displayQuestion ? <AltTest displayQuestion={displayQuestion} /> : <AltSelection updatePage={updatePage} /> }        
      </div>
   )
}

export default AltVerbs
