import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Answer from './Answer';

import _ from 'lodash'

const Question = (props) => {
   const {verb, index, display, handleAnswer} = props
   const [correct, setCorrect] = useState(null)

   const isCorrect = () => {
      if (correct === null) return null
      else if (correct) return (
         <div>&#10004;</div>
      ) 
      else if (!correct) return (
         <div>{`\u2716 - ${verb.fullP}`}</div>
      )
   }

   const handleSubmit = (val) => {
      if (val === verb.conjugationP) {
         setCorrect(true)
         setTimeout(() => handleAnswer(true, verb.id), 1500)
         // handleAnswer(true, verb.id)
      } else {
         setCorrect(false)
         setTimeout(() => handleAnswer(false, verb.id), 3000)
      }
   }

   if (display) {
      return (
         <div id={`question-${index}`} className='question-container'>
            <div className='question-text'>
               <p>{verb.fullE}</p>
            </div>
            <Answer verb={verb} index={index} handleSubmit={handleSubmit} />
            <div className='hint'>
               <p>Hint: {verb.infinitiveP} + {verb.tense}</p>
            </div>
            <div className='result'>{isCorrect()}</div>
         </div>
      )
   } else return null

}

export default Question
