import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Answer from './Answer';

import _ from 'lodash'

const Question = (props) => {
   const {verb, index, display, handleAnswer} = props

   const handleSubmit = (val) => {
      if (val === verb.conjugationP) handleAnswer(true, verb.id)
      else handleAnswer(false, verb.id)
   }

   if (display) {
      return (
         <div id={`question-${index}`} className='question-container'>
            <div className='question-text'>
               <p>{verb.fullE}</p>
            </div>
            <Answer verb={verb} index={index} handleSubmit={handleSubmit} />
            <div className='hint'>
               <p>Hint: {verb.infinitiveP} + {verb.tense}</p>
            </div>
         </div>
      )
   } else return null

}

export default Question
