   //          return (
   //          <input className='answer-input' type='text' />
   //          <div className='pronoun'>{verb.pronounP}</div>
   //       )} else {
   //          return (
   //          <div className='pronoun'>{verb.pronounP}</div>
   //          <input className='answer-input' type='text' />
   //       )}}
   //    </div>
   // )
}

export default Answer
