import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addCorrect, addIncorrect, selectResultTally } from '../app/verbsSlice';

import Question from './Question';

import _ from 'lodash'

const QuestionBox = (props) => {
   const {questionIdArray, filteredVerbs, displayQuestion} = props
   const [activeId, setActiveId] = useState(0)
   const [testComplete, setTestCmplete] = useState(false)

   const dispatch = useDispatch()

   const handleAnswer = (bool, id) => {
      if (bool) dispatch(addCorrect(id))   
      else dispatch(addIncorrect(id))
      let isComplete = useSelector(selectResultTally)
      if (isComplete) window.alert('done!')
      else setActiveId((current) => current + 1)
   }

   const selectedVerb = (i) => filteredVerbs.find(el => el.id === i)

   if (displayQuestion) {
      return (
         <div id='question-box'>{questionIdArray.map((id, index) => <Question verb={selectedVerb(id)} index={index} display={activeId === index} handleAnswer={handleAnswer} key={index} />)}</div>
      )
   } else return null
}

export default QuestionBox
