   const {questionIdArray, filteredVerbs, displayQuestion} = props

   const QuestionArray = () => {
      return questionIdArray.map((index, i) => {
            let verb = filteredVerbs.find(el => el.id === index)
            return <div key={i}><p>{verb.fullP}</p></div>
         })

      // return (

