import { createSlice } from '@reduxjs/toolkit';
import { createVerbObject, filterSelectedVerbTense, shuffleVerbIds } from '../functions/loadingFunctions';
import { tallyResponse } from '../functions/questionFunctions';

export const verbsSlice = createSlice({
   name: 'verbs',
   initialState: {
    // allVerbs: {},
    filteredVerbs: [],
    tenseSelection: [],
    questions: {
      shuffledIds: [],
      correctAnswers: [],
      incorrectAnswers: []
    }
   },
   reducers: {
      loadData: (state, action) => {
        state.allVerbs = action.payload
      },
      selectTense: (state, action) => {
        let filters = action.payload
        let allVerbs = createVerbObject()
        let filteredVerbs = filterSelectedVerbTense(filters, allVerbs)
        let shuffledIds = shuffleVerbIds(filteredVerbs)

        state.tenseSelection = action.payload
        state.filteredVerbs = filteredVerbs
        state.questions.shuffledIds = shuffledIds
      },
      addCorrect: (state, action) => {
        state.questions.correctAnswers.push(action.payload)
      },
      addIncorrect: (state, action) => {
        state.questions.incorrectAnswers.push(action.payload)
        // tallyResponse(state)
      }
   }
})

