import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import _ from 'lodash'

import { loadData, selectTense, selectFilteredAllVerbs } from '../app/verbsSlice';

import createVerbObject from '../functions/loadingFunctions';
import Selection from './Selection';
import Test from './Test';

const Verbs = () => {
   const [selectedTense, setSelectedTense] = useState(['all'])
   const [selectedVerbs, setSelectedVerbs] = useState({})
   const dispatch = useDispatch();
   const isFirstRender = useRef(true)

   const onFirstRender = () => {
      dispatch(loadData(createVerbObject()));
      isFirstRender.current = false
   }

   useEffect(onFirstRender, []);

   const submitTense = () => {
      submitResults()
      .then(() => selectVerbs())
      .then(() => changeDisplay)
   }

   const submitResults = async () => dispatch(selectTense(selectedTense))
   const changeDisplay = async () => document.getElementById('selection').classList.add('hidden')
   const selectVerbs = async () => {
      const results = useSelector(selectFilteredAllVerbs)
      setSelectedVerbs(results)
   }

   // const updateTense = (arr) => setSelectedTense(arr)

   return (
      <div>
         Hello there!
         <Selection submitTense={submitTense} selectedTense={selectedTense} setSelectedTense={setSelectedTense} />
         <Test selectedVerbs={selectedVerbs} />
      </div>
   )
}

export default Verbs
