import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { loadData, selectTense, selectFilteredAllVerbs } from '../app/verbsSlice';

import createVerbObject from '../functions/loadingFunctions';
import Selection from './Selection';
import Test from './Test';

const Verbs = () => {
   const dispatch = useDispatch();
   const [selectedTense, setSelectedTense] = useState(['all'])   
   const [displayQuestion, setDisplayQuestion] = useState(false)

   const isFirstRender = useRef(true)
   const onFirstRender = () => isFirstRender.current = false

   // const onFirstRender = () => {
   //    dispatch(loadData(createVerbObject()));
   //    isFirstRender.current = false
   // }

   // const selectFilteredVerbs = useSelector(selectFilteredAllVerbs)

   useEffect(() => onFirstRender, []);

   const submitTense = () => {
      dispatch(selectTense(selectedTense))
      document.getElementById('selection').classList.add('hidden')
      setDisplayQuestion(true)
   }

   return (
      <div id='verbs'>
         Hello there!
         <Selection submitTense={submitTense} selectedTense={selectedTense} setSelectedTense={setSelectedTense} />
         <Test displayQuestion={displayQuestion} />
      </div>
   )
}

export default Verbs
