import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import _ from 'lodash'

import { loadData, selectTense, selectFilteredAllVerbs } from '../app/verbsSlice';

import createVerbObject from '../functions/loadingFunctions';

const Verbs = () => {

   const allVerbs = useSelector(selectFilteredAllVerbs);
   const dispatch = useDispatch();

   const tenseSelection = useRef(['present', 'past', 'present continuous', 'past continuous', 'present perfect', 'past perfect', 'future perfect', 'imperfect', 'imperative'])
   const [selectedTense, setSelectedTense] = useState([])
   const [isAll, setIsAll] = useState(true)

   const onFirstRender = () => {
     dispatch(loadData(createVerbObject()));
   }
   useEffect(onFirstRender, []);

   const checkHandler = (e) => {
      let val = e.target.value
      let arr = [...selectedTense]
      if (val === 'all') {
         setIsAll(current => !current)
         setSelectedTense('all')
      } else {
         if (_.includes(arr, val)) _.pull(arr, val)
         else arr.push(val)
         console.log(arr)
         setSelectedTense(arr)
      }
      console.log(val)
   }

   return (
      <div>
         Hello there!
         <form>
            <div>
               <input type='checkbox' id='tense0' value='all' onChange={checkHandler} checked={isAll} />
               <label for='tense0'> All</label>
            </div>
            {tenseSelection.current.map((tense, i) => (
               <div>
                  <input type='checkbox' id={`tense${i + 1}`} value={tense} onChange={checkHandler} disabled={isAll} />
                  <label for={`tense${i}`}> {_.upperFirst(tense)}</label>
               </div>
            ))}
            <div><input type='button' value='Ready' onclick='' /></div>
            {/* <input type='checkbox' id='tense1' value='present' />
            <label for='tense1'> Present</label> */}
         </form>
         {/* {Object.keys(allVerbs).map(el => (<p>{el}</p>))} */}
      </div>
   )
}

export default Verbs
